mapper.update:
UPDATE oms_order SET delivery_sn = CASE id
WHEN #{item.orderId} THEN #{item.deliverySn}
END, delivery_sn = CASE id
WHEN #{item.orderId} THEN #{item.deliverySn}
END, delivery_company = CASE id
WHEN #{item.orderId} THEN #{item.deliveryCompany}
END, delivery_time = CASE id
WHEN #{item.orderId} THEN now()
END, `status` = CASE id
WHEN #{item.orderId} THEN 2
END WHERE id IN
#{item.orderId}
AND `status` = 1

mapper.select:
SELECT
*
FROM
oms_order
WHERE
delete_status = 0

    AND order_sn = #{queryParam.orderSn}


    AND `status` = #{queryParam.status}


    AND source_type = #{queryParam.sourceType}


    AND order_type = #{queryParam.orderType}


    AND create_time LIKE concat(#{queryParam.createTime},"%")


    AND (
    receiver_name LIKE concat("%",#{queryParam.receiverKeyword},"%")
    OR receiver_phone LIKE concat("%",#{queryParam.receiverKeyword},"%")
    )
